{/** *
function Validation ({contact})  {
const error ={}
const email_pattern = /[^\s@]+@[^\s@]+.\[^\s@]{2,6}$/;
if(contact.fName===""){
 error.fName ="fName is Required"
}
if(contact.lName===""){
    error.lName="lName is required"
}
if(contact.massage===""){
    error.massage="Massage is Required"
}
if(contact.email===""){
    error.email="Email is Require"
}
 else if (!email_pattern.test(contact.email)){
    error.email="Email did'nt match"
}
return error;
  
}
export default Validation;  **/}
