import * as React from "react";
import { useState } from "react";
import Validation from "./Validation";

function Form() {
  const [contact, setContact] = useState({
    fName: "",
    lName: "",
    email: "",
    massage:""
  });

  const [error , setError] = useState({});
  
  function handleChange(event){
  const { name , value } = event.target; //we pass obj
  
  setContact(preValue =>{  //passing prevalue as parameter
    if(name==="fName"){
      return{
        fName:value,
        lName:preValue.lName,
        email:preValue.email,
        massage:preValue.massage
      };
    }else if(name==="lName"){
      return{
        fName:preValue.fName,
        lName:value,
        email:preValue.email,
        massage:preValue.massage
      };
    }else if(name==="email"){
      return{
        fName:preValue.fName,
        lName:preValue.lName,
        email:value,
        massage:preValue.massage
      };
    }else if(name==="massage"){
        return{
          fName:preValue.fName,
          lName:preValue.lName,
          email:preValue.email,
          massage:value
        };
    }}
  )
  
   
  }
  
  function handleValidation(event){
    event.preventDefault();
   setError( Validation(contact));
  }
  return (
    <div className="pt-5 bg-yellow-50">
       <form className="" onSubmit={handleValidation}   >
       <div className="px-10 py-5 mx-10 my-5 bg-white border border-black"> <label className="">First Name<br/> </label> 
        <input onChange={handleChange} name="fName" title="First Name" value={contact.fName}   className="border-b-2 focus:outline-none"/>
        {error.fName && <p style={{color:"red"}}>{error.fName}</p>}
        
        </div>
        <div className="px-10 py-5 mx-10 my-5 bg-white border border-black">
        <label>Last Name<br/> </label>
        <input onChange={handleChange} name="lName" title="Last Name" value={contact.lName}  className="border-b-2 focus:outline-none"/>
        {error.lName && <p style={{color:"red"}}>{error.lName}</p>}
         
        </div>
        <div className="px-10 py-5 mx-10 my-5 bg-white border border-black">
        <label> Email<br/> </label>
        <input onChange={handleChange} name="email" title="Email" value={contact.email} className="border-b-2 focus:outline-none"/>
        {error.email && <p style={{color:"red"}}>{error.email}</p>}
        
        </div>
        <div className="px-10 py-5 mx-10 my-5 bg-white border border-black">
        <label> Write Massage<br/> </label>
        <textarea onChange={handleChange} type="massage" name="massage" title="Write Massage" value={contact.massage} className="border-b-2 focus:outline-none"/>
        {error.massage && <p style={{color:"red"}}>{error.massage}</p>}
        
        </div>
        <button className="text-center"  >Submit</button>
      </form>
    </div>
  );
  }

export default Form;
